package com.java;

public class DeclaracionVariables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Declaración de Variables
		 
		int edad = 10; // Numérico entero
		long codigoPostal = 55773; // Numérico largo
		double altura = 1.70; // Numérico decimal
		char genero = 'H'; // Caracter (valores: H: hombre, M: mujer)
        String nombre = "Erik"; // Cadena
        int numeroDeHijos = 2; // Númerico entero
		double iva = 0.16; // Numérico decimal
		int tallaCamisa = 40; // Numérico entero
		double peso = 80.5; // Numérico decimal
		double precio = 25.40; // Numérico decimal
		String alumnoRepetidor = "NO"; // Cadena
		String mensaje = "Hola"; // Cadena
		char letra = 'E'; // Caracter
		int mayorEdad = 18; // Numérico entero
		int minutos = 30; // Numérico entero
		int dias = 365; //  Numérico entero
		String matriculaCoche = "ABC-123";
		int contador = 1; // Numérico entero
        String mayorDeEdad = "Verdadero"; // Cadena (valores: Verdadero, Falso)
        String tallaCamiseta = "XL"; // Cadena (Valores: S, L, XL)
        
        
        //Impresión de Variables
        
        System.out.println("Las variables declaradas son:\n ");
        
        System.out.println("Variable\tValor\tTipo de Variable\n");
        System.out.println("edad\t\t"+    edad+"\t"      +"int");
        System.out.println("codigoPostal\t"+    codigoPostal+"\t"      +"long");
        System.out.println("altura\t\t"+    altura+"\t"      +"double");
        System.out.println("género\t\t"+    genero+"\t"      +"char");
        System.out.println("nombre\t\t"+    nombre+"\t"      +"string");
        System.out.println("numeroDeHijos\t"+    numeroDeHijos+"\t"      +"int");
        System.out.println("iva\t\t"+    iva+"\t"      +"double");
        System.out.println("tallaCamisa\t"+    tallaCamisa+"\t"      +"int");
        System.out.println("peso\t\t"+    peso+"\t"      +"double");
        System.out.println("precio\t\t"+    precio+"\t"      +"double");
        System.out.println("alumnoRepetidor\t"+    alumnoRepetidor+"\t"      +"string");
        System.out.println("mensaje\t\t"+    mensaje+"\t"      +"string");
        System.out.println("letra\t\t"+    letra+"\t"      +"char");
        System.out.println("mayorEdad\t"+    mayorEdad+"\t"      +"int");
        System.out.println("minutos\t\t"+    minutos+"\t"      +"int");
        System.out.println("días\t\t"+    dias+"\t"      +"int");
        System.out.println("matriculaCoche\t"+    matriculaCoche+"\t"      +"string");
        System.out.println("contador\t"+    contador+"\t"      +"int");
        System.out.println("mayorDeEdad\t"+    mayorDeEdad+"\t"      +"string");
        System.out.println("tallaCamiseta\t"+    tallaCamiseta+"\t\t"      +"string");

	}

}
