package com.java;

public class OperacionesAritmeticas {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		double a = 8.12;
		double b = 5.60;
		double c = 1.75;
		
		double suma = a+b+c;
		double resta = a-b-c;
		double mutiplicacion = a*b*c;
		double division = a/b/c;
		
		System.out.println("La suma de las variables es: "+ suma);
		System.out.println("La resta de las variables es: "+ resta);
		System.out.println("La multiplicacion de las variables es: "+ mutiplicacion);
		System.out.println("La division de las variables es: "+ division);
		
	}

}
